import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React from 'react'
import { signOut } from 'firebase/auth'
import { auth } from '../firebaseConfig'
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
/*
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import React from 'react'
import { useNavigation } from '@react-navigation/native'
import { signOut } from 'firebase/auth';
import { auth } from '../firebase';
//import { createUser, updateUser, deleteUser} from './UserScreen';
*/

const HomeScreen = () => {

  const stack = createNativeStackNavigator();
  
  const navigation = useNavigation();

  const handleSignOut = () => {
    signOut(auth) 
    .then(() => {
      navigation.replace("Login")
    })
    .catch(error => alert(error.message))
  }

  const gradesScreenRedir = () => {
    navigation.replace("AddGrades")
  }

  const StudentScreenRedir = () => {
    navigation.replace("Grades");
  }

  return (
    <View>
      <Text>Email: {auth.currentUser?.email}</Text>
      <TouchableOpacity
        onPress={handleSignOut}
        style={styles.button}
        >
          <Text styles={styles.button}>Sign Out</Text>
        </TouchableOpacity>
        <TouchableOpacity
          onPress={gradesScreenRedir}
          style={styles.button}>
            <Text styles={styles.buttonText}>Add Grades</Text>

        </TouchableOpacity>
        <TouchableOpacity
          onPress={StudentScreenRedir}
          style={styles.button}>
            <Text styles={styles.buttonText}>Grades</Text>

        </TouchableOpacity>

        <TouchableOpacity
          onPress={StudentScreenRedir}
          style={styles.button}>
            <Text styles={styles.buttonText}>Students</Text>

        </TouchableOpacity>
    </View>
  )
}

export default HomeScreen

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  button: {
    backgroundColor: '#0782F9',
    width: '60%',
    padding: 15,
    borderRadius: 10,
    alignItems: 'center',
    marginTop: 40,
  },
  buttonOutline: {
      backgroundColor: 'white',
      marginTop: 5,
      borderColor: '#0782F9',
      borderWidth: 2,
  },
  buttonText: {
      color: 'white',
      fontWeight: '700',
      fontSize: 16,
  },
})
