import { StyleSheet, Text, View, TouchableOpacity, Button } from 'react-native'
import React, { useState, useEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { db } from '../firebaseConfig';
import { collection, doc, getDocs, updateDoc, deleteDoc } from 'firebase/firestore';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';
import { DataTable } from 'react-native-paper';

const EditStudent = () => {
    const [students, setStudents] = useState([]);
    const [grades, setGrades]= useState([]);
    const [userDoc, setUserDoc] = useState(null)
    const usersCollectionRef = collection(db, "Students");


    const navigation = useNavigation();

    const HomeScreenRedir = () => {
        navigation.replace("Home");
        }

        

        
        useEffect(() => {
            const getStudents = async () => {
                const data = await getDocs(usersCollectionRef);
                //console.log(data);
                setStudents(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id }))); // now users are added to the db
                // users are added, but not displayed before I push the refresh button (update is next)
            }; 
            
            getStudents();
            } ,[]);

            
         
        
       

    return (
        
        <View>
            <TouchableOpacity
                onPress={HomeScreenRedir}
                style={styles.button}>
                <Text styles={styles.buttonText}>Go Home</Text>
            </TouchableOpacity>
            {students.map((student) => {
               <Text>Test</Text>
        })}
            
            
            
            
        </View>
    );
}

export default EditStudent

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
  })
