  const Read = () => {
    // MARK: Reading Doc
    // You can read what ever document by changing the collection and document path here
    const myDoc = doc(db, "Students", studentid)

    getDoc(myDoc)
      // Handling Promises
      .then((snapshot) => {
        // MARK: Success
        if (snapshot.exists) {
          setUserDoc(snapshot.data())
        }
        else {
          alert("No Doc Found")
        }
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })

  }

  const Update = (value, merge) => {
    // MARK: Updating Doc
    const myDoc = doc(db, "Students", studentid);

    // If you set merge true then it will merge with existing doc otherwise it will be a fresh one
    setDoc(myDoc, value, { merge: merge })
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Updated Successfully!")
        setText("")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })
  }

  const Delete = () => {
    // MARK: Deleting Doc
    const myDoc = doc(db, "Student", studentid)

    deleteDoc(myDoc)
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Deleted Successfully!")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })

  }

  return (
    <View style={styles.container}>
      <Button title='Read Doc' onPress={Read}></Button>
      {
        userDoc != null &&
        <Text>{userDoc.fName} {userDoc.lName} / {userDoc.DOB}: MGMT329 Score: {userDoc.MGMT329Score || 'Ikke deltatt'} og MGMT450 Score: {userDoc.MGMT450Score || 'Ikke deltatt'}, </Text>
      }
      <View>
	 <DataTable>
				<DataTable.Header>
				<DataTable.Title>fName</DataTable.Title>
                <DataTable.Title>fName</DataTable.Title>
                <DataTable.Title>fName</DataTable.Title>
				</DataTable.Header>
	<DataTable.Row>
					<DataTable.Cell>
                        <TextInput placeholder='Type fname' onChangeText={(text) => { setText(text) }} value={fName}></TextInput>
                    </DataTable.Cell>
                    <DataTable.Cell>
                        <TextInput placeholder='Type lname' onChangeText={(text) => { setText2(text) }} value={lName}></TextInput>
                    </DataTable.Cell>
                    <DataTable.Cell>
                        <TextInput placeholder='Type dob' onChangeText={(text) => { setText3(text) }} value={dob}></TextInput>
                        </DataTable.Cell>
				</DataTable.Row>

		</DataTable>
</View>

      <Button title='Update fName' onPress={() => {
        Update({
          "fName": fName,
          "lName": fName,
          "DOB": dob
        }, true)
      }} disabled={fName == ""}></Button>
      <Button title='Update lName' onPress={() => {
        Update({
          "lName": lName
        }, true)
      }} disabled={lName == ""}></Button>
      <Button title='Update DOB' onPress={() => {
        Update({
          "DOB": dob
        }, true)
      }} disabled={dob == ""}></Button>
      <Button title='Delete Doc' onPress={Delete}></Button>
    </View>
  );
}


