import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React, { useState, useEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { db } from '../firebaseConfig';
import { collection, doc, getDocs, updateDoc, deleteDoc } from 'firebase/firestore';

const StudentsScreen = () => {
    const [newName, setNewName] = useState("");
    const [newAge, setNewAge] = useState(0);
    const [users, setUsers] = useState([]);
    const usersCollectionRef = collection(db, "users");


    const navigation = useNavigation();

    const HomeScreenRedir = () => {
        navigation.replace("Home");
        }

        useEffect(() => {
            const getUsers = async () => {
              const data = await getDocs(usersCollectionRef);
              console.log(data);
              setUsers(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id }))); // now users are added to the db
              // users are added, but not displayed before I push the refresh button (update is next)
            }; 
            
            getUsers();
          } ,[]);

    return (
        <View>
            <Text>Students Screen is best</Text>
            <TouchableOpacity
                onPress={HomeScreenRedir}
                style={styles.button}>
                <Text styles={styles.buttonText}>Go Home</Text>
            </TouchableOpacity>
            {users.map((user) => {
        return (
            <View>
                <Text>User: {user.name}</Text>
                <Text>Age: {user.age}</Text>
            </View>
        );
      })}
        </View>
    );
}

export default StudentsScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
  })
