import { Button, StyleSheet, Text, TextInput, View, TouchableOpacity } from 'react-native';
import { useState, useEffect, useRef } from 'react';
import { db } from '../firebaseConfig';
import { deleteDoc, doc, getDoc, getDocs, collection, setDoc } from 'firebase/firestore';
import { DataTable } from 'react-native-paper';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';

const EditStudent = ({route}) => {
    // Storing User Data
  const [userDoc, setUserDoc] = useState(null)
  const usersCollectionRef = collection(db, "Students");
  // Update Text
  const [text, setText] = useState("");
  const _fname = "";
  const [fName, setFName] = useState("");
  const [lName, setLName] = useState("");
  const [dob, setDob] = useState("");

  const [students, setStudents] = useState([]);
  const studentid = route.params.paramKey;
  console.log("is this undefined?" + route.params.paramKey);
  
  const {current:a} = useRef(['a'])

 
  useEffect(() => {
    const getStudents = async () => {
        const data = await getDocs(usersCollectionRef);
        //console.log(data);
        setStudents(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id }))); // now users are added to the db
        // users are added, but not displayed before I push the refresh button (update is next)
        
    }; 
    getStudents();
    
    } ,[a]);
    

  const Update = (value, merge) => {
    // MARK: Updating Doc
    const myDoc = doc(db, "Students", studentid);

    // If you set merge true then it will merge with existing doc otherwise it will be a fresh one
    setDoc(myDoc, value, { merge: merge })
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Updated Successfully!")
        setText("")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })
  }

  const Delete = () => {
    // MARK: Deleting Doc
    const myDoc = doc(db, "Student", studentid)

    deleteDoc(myDoc)
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Deleted Successfully!")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })

  }

  {students.filter(s => s.id == studentid).forEach(stud => {
    setFName(stud.fName);
    setLName(stud.lName);
    setDob(stud.DOB);
    console.log("checkig stud.fName and fName:");
    console.log(stud.fName);
    console.log(fName);
    })}

  return (
    <View style={styles.container}>
        
      <View>
	 <DataTable>
				<DataTable.Header>
				<DataTable.Title>fName</DataTable.Title>
                <DataTable.Title>fName</DataTable.Title>
                <DataTable.Title>fName</DataTable.Title>
				</DataTable.Header>
	<DataTable.Row>
   
					<DataTable.Cell>
                        <TextInput placeholder="ral{fName}" onChangeText={(text) => { setFName(text) }} value={fName}></TextInput>
                    </DataTable.Cell>
                    <DataTable.Cell>
                        <TextInput placeholder='Type lname' onChangeText={(text) => { setLName(text) }} value={lName}></TextInput>
                    </DataTable.Cell>
                    <DataTable.Cell>
                        <TextInput placeholder='Type dob' onChangeText={(text) => { setDob(text) }} value={dob}></TextInput>
                        </DataTable.Cell>

				</DataTable.Row>

		</DataTable>
</View>

      <Button title='Update User' onPress={() => {
        Update({
          "fName": fName,
          "lName": lName,
          "DOB": dob
        }, true)
      }} disabled={fName == ""}></Button>
      
    </View>
  );
}
    export default EditStudent

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
  })
