import { Button, StyleSheet, Text, TextInput, View, TouchableOpacity } from 'react-native';
import { useState, useEffect } from 'react';
import { db } from '../firebaseConfig';
import { deleteDoc, doc, getDoc, collection, setDoc } from 'firebase/firestore';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';

const EditStudent = (studentid) => {
    // Storing User Data
  const [userDoc, setUserDoc] = useState(null)
  // Update Text
  const [text, setText] = useState("")
  const [text2, setText2] = useState("")
  const [text3, setText3] = useState("")

  // MARK: CRUD Functions
  const Create = () => {
    // MARK: Creating New Doc in Firebase
    // Before that enable Firebase in Firebase Console
    const myDoc = doc(db, "Students", "MyDocument")

    // Your Document Goes Here
    const docData = {
      "name": "iJustine",
      "bio": "YouTuber"
    }

    setDoc(myDoc, docData)
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Document Created!")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })
  }

  const Read = () => {
    // MARK: Reading Doc
    // You can read what ever document by changing the collection and document path here
    const myDoc = doc(db, "Students", "333prceqcFdn0okwkDb9")

    getDoc(myDoc)
      // Handling Promises
      .then((snapshot) => {
        // MARK: Success
        if (snapshot.exists) {
          setUserDoc(snapshot.data())
        }
        else {
          alert("No Doc Found")
        }
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })

  }

  const Update = (value, merge) => {
    // MARK: Updating Doc
    const myDoc = doc(db, "Students", "333prceqcFdn0okwkDb9")

    // If you set merge true then it will merge with existing doc otherwise it will be a fresh one
    setDoc(myDoc, value, { merge: merge })
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Updated Successfully!")
        setText("")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })
  }

  const Delete = () => {
    // MARK: Deleting Doc
    const myDoc = doc(db, "MyCollection", "MyDocument")

    deleteDoc(myDoc)
      // Handling Promises
      .then(() => {
        // MARK: Success
        alert("Deleted Successfully!")
      })
      .catch((error) => {
        // MARK: Failure
        alert(error.message)
      })

  }

  return (
    <View style={styles.container}>
      <Button title='Create New Doc' onPress={Create}></Button> 
      <Button title='Read Doc' onPress={Read}></Button>
      {
        userDoc != null &&
        <Text>{userDoc.fName} {userDoc.lName} / {userDoc.DOB}: MGMT329 Score: {userDoc.MGMT329Score || 'Ikke deltatt'} og MGMT450 Score: {userDoc.MGMT450Score || 'Ikke deltatt'}, </Text>
      }
      <TextInput style={{
        width: '95%',
        fontSize: 18,
        padding: 12,
        borderColor: 'gray',
        borderWidth: 0.2,
        borderRadius: 10,
        marginVertical: 20
      }} placeholder='Type fname' onChangeText={(text) => { setText(text) }} value={text}></TextInput>
      <TextInput style={{
        width: '95%',
        fontSize: 18,
        padding: 12,
        borderColor: 'gray',
        borderWidth: 0.2,
        borderRadius: 10,
        marginVertical: 20
      }} placeholder='Type lname' onChangeText={(text) => { setText2(text) }} value={text2}></TextInput>
      <TextInput style={{
        width: '95%',
        fontSize: 18,
        padding: 12,
        borderColor: 'gray',
        borderWidth: 0.2,
        borderRadius: 10,
        marginVertical: 20
      }} placeholder='Type dob' onChangeText={(text) => { setText3(text) }} value={text3}></TextInput>

      <Button title='Update fName' onPress={() => {
        Update({
          "fName": text
        }, true)
      }} disabled={text == ""}></Button>
      <Button title='Update fName' onPress={() => {
        Update({
          "fName": text2
        }, true)
      }} disabled={text2 == ""}></Button>
      <Button title='Update fName' onPress={() => {
        Update({
          "fName": text3
        }, true)
      }} disabled={text3 == ""}></Button>
      <Button title='Delete Doc' onPress={Delete}></Button>
    </View>
  );
}


