import { useState, useEffect } from 'react';
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { db } from '../firebaseConfig';
import { deleteDoc, doc, getDoc, collection, setDoc } from 'firebase/firestore';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';
import { DataTable } from 'react-native-paper';
import StudentsScreen from './StudentsScreen';

const EditStudent = (studentid) => {
    const [ fName, setFName] = useState("");
    const [ lName, setLName] = useState("");
    const [ dob, setDOB] = useState("");
    const [grades, setGrades]= useState([]);
    const [userDoc, setUserDoc] = useState(null)
    const studentsRef = collection(db, "Students");
        
    // snapshot:
    // https://youtu.be/LGn6E817XQ4?t=606
    useEffect(() => {
        const getStudents = async () => {
            const data = await getDocs(studentsRef);
            data.forEach((student) => {
                console.log(student.id);  
                myIDS.push(student.id); 
            });
            setStudents(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id })));
        }
        getStudents();
    },[])

    const Update = (value, merge) => {
        // MARK: Updating Doc
        const myDoc = doc(db, "MyCollection", "MyDocument")
    
        // If you set merge true then it will merge with existing doc otherwise it will be a fresh one
        setDoc(myDoc, value, { merge: merge })
          // Handling Promises
          .then(() => {
            // MARK: Success
            alert("Updated Successfully!")
            setText("")
          })
          .catch((error) => {
            // MARK: Failure
            alert(error.message)
          })
      }

    return (
        <View>
            <TouchableOpacity
                onPress={HomeScreenRedir}
                style={styles.button}>
                <Text styles={styles.buttonText}>HOme</Text>
                {myIDS.forEach((stu) => {
                    <Text>{stu}</Text>
                })}
            </TouchableOpacity>
            <View>
                        <DataTable>
                            <DataTable.Header>
                            <DataTable.Title>fName</DataTable.Title>
                            <DataTable.Title>lName</DataTable.Title>
                            <DataTable.Title>DOB</DataTable.Title>
                            </DataTable.Header>

            {students.map((student) => {
                return (
                <DataTable.Row>
                    <DataTable.Cell><TextInput placeholder='Type Here' onChangeText={(text) => { setFName(text) }} value={text}></TextInput>{student.fName}</DataTable.Cell>
                    <DataTable.Cell><TextInput placeholder='Type Here' onChangeText={(text) => { setLName(text) }} value={text}></TextInput>{student.fName}</DataTable.Cell>
                    <DataTable.Cell><TextInput placeholder='Type Here' onChangeText={(text) => { setDOB(text) }} value={text}></TextInput>{student.fName}</DataTable.Cell>
                                
                            </DataTable.Row>
                );
            })}
            </DataTable>
            <button title="Edit Studnet" onPress={() => {

            }} ></button>
            </View>
            
        </View>
    );
}


