import { StyleSheet, Button, Text, TextInput, View, TouchableOpacity } from 'react-native'
import React, { useState, useEffect } from 'react'
import { collection, updateDoc, doc, deleteDoc, setDoc } from 'firebase/firestore'; 
import { db } from '../firebaseConfig'
import { useNavigation } from '@react-navigation/native'
import { SafeAreaInsetsContext } from 'react-native-safe-area-context';



const AddStudent = () => {
    const [fname, setFname] = useState("");
    const [lname, setLname] = useState("");
    const [dob, setDob] = useState("");

    const [newFName, setNewFName] = useState("");
    const [newLName, setNewLName] = useState("");
    const [newDOB, setNewDOB] = useState("");
    const [text, setText] = useState("");
    const usersCollectionRef = collection(db, "Students");

    const CreateStudent = (fname, lname, dob) => {
        // const myDoc = doc(db, "MyCollection", "MyDocument");
        const myDoc = doc(db, "Students", "akka" + fname + lname + dob)
        
        // Your document goes here
        const docData = {
            "fName": "Helge",
            "lName": "STegemoen",
            "DOB": "1971"
        }
        // Hvordan bruke create med knapp
        // Hvordan redirecte til en ny side for Ã¥ redigere studenten?
        // Hvordan sende et studentobjekt til en ny side for redigering?
        
        setDoc(myDoc, docData)
        // Handling promises
        .then(() => {
            alert("document.created")
        })
        .catch((error) => {
            alert(error.message);
        })

    }

    const navigation = useNavigation();

    const HomeScreenRedir = () => {
        navigation.replace("Home");
        }

        return (
            <View style={styles.container}>
                <TextInput placeholder="fName" value={fname} onChangeText={(text) => setFname(text)}></TextInput>
                <TextInput placeholder="lName" value={lname} onChangeText={(text) => setLname(text)}></TextInput>
                <TextInput placeholder="DOB" value={dob} onChangeText={(text) => setDob(text)}></TextInput>
                <TouchableOpacity
                    onPress={CreateStudent(fname, lname, dob)}
                >
                    <Text>Add Student</Text>

                </TouchableOpacity>
                <Button title='Add Student' onPress={() => {
                    Create({
                        "bio": text
                    }, true)
                }} disabled={text == ""}></Button>
            </View>
          );
}

export default AddStudent

const styles = StyleSheet.create({})
