import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React, { useState, useEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { db } from '../firebaseConfig';
import { collection, doc, getDocs, updateDoc, deleteDoc } from 'firebase/firestore';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';
import { DataTable } from 'react-native-paper';

const StudentScreen = () => {
    const [students, setStudents] = useState([]);
    const [grades, setGrades]= useState([]);
    const [userDoc, setUserDoc] = useState(null)
    const usersCollectionRef = collection(db, "Students");


    const navigation = useNavigation();

    const HomeScreenRedir = () => {
        navigation.replace("Home");
        }

        const Read = () => {
            const myDoc = doc(db, "Students", "Bigg.Bagg.0909909");
            
            getDoc(myDoc)
            // Handling promises
            .then((snapshot) => {
                if(snapshot.exists){
                    setUserDoc(snapshot.data())
                } else {
                    alert("No doc found");
                }
            })
            .catch(error => {
                alert(error.message);
            })
        }

    return (
        <View>
            <TouchableOpacity
                onPress={HomeScreenRedir}
                style={styles.button}>
                <Text styles={styles.buttonText}>Go Home</Text>
            </TouchableOpacity>
            <View>
                        <DataTable>
                            <DataTable.Header>
                            <DataTable.Title>fName</DataTable.Title>
                            <DataTable.Title>lName</DataTable.Title>
                            <DataTable.Title>DOB</DataTable.Title>
                            </DataTable.Header>

            {students.map((student) => {
                return (
                <DataTable.Row>
                                <DataTable.Cell>{student.fName}</DataTable.Cell>
                                <DataTable.Cell>{student.lName}</DataTable.Cell>
                                <DataTable.Cell>{student.DOB}</DataTable.Cell>
                            </DataTable.Row>
                );
            })}
            </DataTable>
            </View>
            
        </View>
    );
}

export default StudentScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
  })
