import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React, { useState, useEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { createNativeStackNavigator } from '@react-navigation/native-stack'
import { db } from '../firebaseConfig';
import { collection, doc, getDocs, updateDoc, deleteDoc } from 'firebase/firestore';
//import { Table, TableWrapper, Row, Rows, Col, Cols, Cell } from 'react-native-table-component';
import { DataTable } from 'react-native-paper';

const StudentsScreen = () => {
    const [newName, setNewName] = useState("");
    const [newAge, setNewAge] = useState(0);
    const [students, setStudents] = useState([]);
    const [grades, setGrades]= useState([]);
    const usersCollectionRef = collection(db, "Students");


    const navigation = useNavigation();

    const HomeScreenRedir = () => {
        navigation.replace("Home");
        }

        useEffect(() => {
            const getStudents = async () => {
              const data = await getDocs(usersCollectionRef);
              //console.log(data);
              setStudents(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id }))); // now users are added to the db
              // users are added, but not displayed before I push the refresh button (update is next)
            }; 
            
            getStudents();
          } ,[]);

    return (
        <View>
            <Text>Students Screen is best</Text>
            <TouchableOpacity
                onPress={HomeScreenRedir}
                style={styles.button}>
                <Text styles={styles.buttonText}>Go Home</Text>
            </TouchableOpacity>
            {students.filter(student => student.MGMT329Score > 0).map((student) => {
                return (
                    
                    <View>
                        <DataTable>
                            <DataTable.Header>
                            <DataTable.Title>classId</DataTable.Title>
                            <DataTable.Title>fName</DataTable.Title>
                            <DataTable.Title>lName</DataTable.Title>
                            <DataTable.Title>DOB</DataTable.Title>
                            <DataTable.Title>className</DataTable.Title>
                            <DataTable.Title numeric>Score</DataTable.Title>
                            <DataTable.Title>Grade</DataTable.Title>
                            </DataTable.Header>

                            <DataTable.Row>
                            <DataTable.Cell>John</DataTable.Cell>
                            <DataTable.Cell>john@kindacode.com</DataTable.Cell>
                            <DataTable.Cell numeric>33</DataTable.Cell>
                            </DataTable.Row>

                            <DataTable.Row>
                            <DataTable.Cell>Bob</DataTable.Cell>
                            <DataTable.Cell>test@test.com</DataTable.Cell>
                            <DataTable.Cell numeric>105</DataTable.Cell>
                            </DataTable.Row>

                            <DataTable.Row>
                            <DataTable.Cell>Mei</DataTable.Cell>
                            <DataTable.Cell>mei@kindacode.com</DataTable.Cell>
                            <DataTable.Cell numeric>23</DataTable.Cell>
                            </DataTable.Row>

                        </DataTable>
                        <Text>MGMT329: Student: {student.fName}</Text>
                        <Text>Birthday: {student.DOB}</Text>
                        <Text>MGMT329 Grade: {student.MGMT329Grade}</Text>
                    </View>
                );
            })}
            {students.filter(student => student.MGMT450Score > 0).map((student) => {
                return (
                    <View>
                        <Text>MGMT450: Student: {student.fName}</Text><Text>Birthday: {student.DOB}</Text>
                        <Text>MGMT450 Grade: {student.MGMT450Grade}</Text>
                    </View>
                );
            })}
        </View>
    );
}

export default StudentsScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
        fontSize: 16,
    },
  })
