import { StyleSheet, Text, View, TouchableOpacity } from 'react-native'
import React, { useState, useEffect } from 'react'
import { useNavigation } from '@react-navigation/native'
import { db } from '../firebaseConfig';
import { collection, doc, getDocs, updateDoc, deleteDoc } from 'firebase/firestore';
import {
    LineChart
  } from "react-native-chart-kit";
  import { Dimensions } from "react-native";
  const screenWidth = Dimensions.get("window").width;

const GradesChartScreen = () => {const [students, setStudents] = useState([]);
    const usersCollectionRef = collection(db, "Students");
    const [mgmt329Dataset, setMgmt329Dataset] = useState([]);
    const [mgmt329Labels, setMgmt329Labels] = useState([]);

    const navigation = useNavigation();
    const HomeScreenRedir = () => {
      navigation.replace("Home");
      }
  

        useEffect(() => {
            const getStudents = async () => {
              const data = await getDocs(usersCollectionRef);
              setStudents(data.docs.map((doc)=> ({ ...doc.data(), id: doc.id })));
            }; 
            const setStuds = async () => {
                const valid329Students = []
                const valid329Scores = []
                  students.filter(s => s.MGMT329Score >= 0).forEach(stud => {
                      valid329Scores.push(parseInt(stud.MGMT329Score));
                      console.log(s.MGMT329Score);
                      valid329Students.push(stud.fName.charAt(0) + ". " + stud.lName.charAt(0));
                      
                })
                setMgmt329Dataset(valid329Students);
                setMgmt329Labels(valid329Students);
                console.log(valid329Scores);
            }
        
            getStudents();
            setStuds();
          } ,[]);
          /*
          const valid329Students = []
          const valid329Scores = []
          students.filter(s => s.MGMT329Score >= 0).forEach(stud => {
                valid329Scores.push(parseInt(stud.MGMT329Score));
                valid329Students.push(stud.fName.charAt(0) + ". " + stud.lName.charAt(0));
          })
          setMgmt329Dataset(valid329Students);

          const valid450Students = []
          const valid450Scores = []
          const sampleScores = [20, 50, 30, 90, 25, 34, 100]
          let i = 0;
          students.filter(s => s.MGMT450Score >= 0).forEach(stud => {
            valid450Scores[i] = stud.MGMT450Score
            i++;
              valid450Students.push(stud.fName.charAt(0) + ". " + stud.lName.charAt(0));
          })
          */

    return (
        <View>
      
<Text>MGMT450 Scores</Text>
      <LineChart
        data={{
          labels: mgmt329Labels,
          datasets: [
            {
              data: mgmt329Dataset
            }
          ]
        }}
        width={Dimensions.get("window").width} // from react-native
        height={220}
        yAxisLabel="f"
        yAxisSuffix="f"
        yAxisInterval={1} // optional, defaults to 1
        chartConfig={{
          backgroundColor: "#e26a00",
          backgroundGradientFrom: "#fb8c00",
          backgroundGradientTo: "#ffa726",
          decimalPlaces: 0, // optional, defaults to 2dp
          color: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          labelColor: (opacity = 1) => `rgba(255, 255, 255, ${opacity})`,
          style: {
            borderRadius: 16
          },
          propsForDots: {
            r: "6",
            strokeWidth: "2",
            stroke: "#ffa726"
          }
        }}
        bezier
        style={{
          marginVertical: 8,
          borderRadius: 16
        }}
      />
      
    </View>
    );
}

export default GradesChartScreen

const styles = StyleSheet.create({
    container: {
      flex: 1,
      justifyContent: 'center',
      alignItems: 'center'
    },
    button: {
      backgroundColor: '#0782F9',
      width: '60%',
      padding: 15,
      borderRadius: 10,
      alignItems: 'center',
      marginTop: 40,
    },
    buttonOutline: {
        backgroundColor: 'white',
        marginTop: 5,
        borderColor: '#0782F9',
        borderWidth: 2,
    },
    buttonText: {
        color: 'white',
        fontWeight: '700',
